mapper.select:
select user_id, user_pw, user_name, user_sex, user_age, user_role, user_oauth, user_exit
from users
where user_id = #{userId}

mapper.update:
update users
set user_pw = #{userPw}, user_name = #{userName}, user_sex = #{userSex}, user_age = #{userAge}, user_role = #{userRole}
where user_id = #{userId}

mapper.select:
select user_id, user_name
from users
where user_id = #{userId} and user_pw = #{userPw}
